package database;import product.Product;import java.util.ArrayList;import java.util.Collections;/** * Used to store the products data */public class ProductDatabase {    private ArrayList<Product> database;    /**     *     */    public ProductDatabase() {        this.database = new ArrayList<>();    }    /**     * @param product is the product to add to this database     */    public void put(Product product){ database.add(product); }    /**     * @param code the code of the product to look for     * @return     */    public Product get(int code){        final Product[] p = new Product[1];        database.forEach(product -> {            if (product.getCode() == code){                p[0] =product;            }        });        if (p[0] !=null) return p[0];        else throw (new NullPointerException("The product was not found"));    }    /**     * @return returns the unique products in the db     */    public ArrayList<Product> getProducts(){        ArrayList<Product>  uniqueProducts=new ArrayList<>();        database.forEach(product -> {            if (!uniqueProducts.contains(product)){                uniqueProducts.add(product);            }        });        Collections.sort(uniqueProducts, new Product.PriceSorter());        return uniqueProducts;    }}